package org.htw.prog2.aufgabe1;

import org.htw.prog2.aufgabe1.exceptions.FileFormatException;
import org.junit.jupiter.api.Test;

import java.io.FileNotFoundException;

import static org.junit.jupiter.api.Assertions.*;

class SeqFileTest {

    @Test
    void throws_doesNotExist() {
        Exception e = assertThrows(FileNotFoundException.class, () -> {
            SeqFile seqfile = new SeqFile("data/DOESNOTEXIST");
        });
    }

    @Test
    void throws_wrongFormat() {
        Exception e = assertThrows(FileFormatException.class, () -> {
            SeqFile seqfile = new SeqFile("data/GREB1_patterns.csv");
        });
        assertEquals("FASTA File does not start with sequence header line.", e.getMessage());
    }

    @Test
    void doesntThrow_correctFormat() {
        assertDoesNotThrow(() -> {
            SeqFile seqfile = new SeqFile("data/GREB1_reference.fasta");
        });
    }

    @Test
    void getNumberOfSequences_single() {
        SeqFile seqfile = null;
        try {
            seqfile = new SeqFile("data/GREB1_reference.fasta");
        } catch(Exception e) {
            fail("Es sollte keine Exception fliegen, es fliegt aber eine " + e.getMessage() + ".");
        }
        assertEquals(1, seqfile.getNumberOfSequences());
    }

    @Test
    void getNumberOfSequences_multiple() {
        SeqFile seqfile = null;
        try {
            seqfile = new SeqFile("data/site_sequences.fasta");
        } catch(Exception e) {
            fail("Es sollte keine Exception fliegen.");
        }
        assertEquals(382, seqfile.getNumberOfSequences());
    }

    @Test
    void getSequences_single() {
        SeqFile seqfile = null;
        try {
            seqfile = new SeqFile("data/GREB1_reference.fasta");
        } catch(Exception e) {
            fail("Es sollte keine Exception fliegen.");
        }
        assertEquals(1, seqfile.getSequences().size());
        assertTrue(seqfile.getSequences().contains(""));
    }

    @Test
    void getSequences_multiple() {
        SeqFile seqfile = null;
        try {
            seqfile = new SeqFile("data/site_sequences.fasta");
        } catch(Exception e) {
            fail("Es sollte keine Exception fliegen.");
        }
        assertEquals(382, seqfile.getSequences().size());
        assertTrue(seqfile.getSequences().contains(""));
        assertTrue(seqfile.getSequences().contains(""));
    }

    @Test
    void getFirstSequence_single() {
        SeqFile seqfile = null;
        try {
            seqfile = new SeqFile("data/GREB1_reference.fasta");
        } catch(Exception e) {
            fail("Es sollte keine Exception fliegen.");
        }
        assertEquals("", seqfile.getFirstSequence());
    }

    @Test
    void getFirstSequence_multiple() {
        SeqFile seqfile = null;
        try {
            seqfile = new SeqFile("data/site_sequences.fasta");
        } catch(Exception e) {
            fail("Es sollte keine Exception fliegen.");
        }
        assertEquals("", seqfile.getFirstSequence());
    }
}